import * as React from "react"
import { cva, type VariantProps } from "class-variance-authority"

import { cn } from "../lib/cn"

const inputVariants = cva(
    "flex h-9 w-full rounded-xl border border-border px-3 py-1 text-sm shadow-sm transition-colors max-w-sm items-center gap-1.5 file:border-0 file:bg-transparent file:text-sm file:font-medium placeholder:text-muted-foreground focus-visible:outline-none focus-visible:ring-1 focus-visible:ring-ring",
    {
        variants: {
            variant: {
                default: "bg-base1",
                main: "bg-main",
                base2: "bg-base2",
                base3: "bg-base3",
                disabled: "cursor-not-allowed opacity-50"
            }

        },
        defaultVariants: {
            variant: "default",

        }


    }






)

export interface InputProps
    extends React.InputHTMLAttributes<HTMLInputElement>,
    VariantProps<typeof inputVariants> {
    asChild?: boolean
}


const Input = React.forwardRef<HTMLInputElement, InputProps>(
    ({ className, variant, size, type, ...props }, ref) => {
        return (
            <input
                className={cn(
                    inputVariants({ variant, className })
                )}
                type={type}

                ref={ref}
                {...props}
            />
        )
    }
)
Input.displayName = "Input"

export { Input, inputVariants }
